<script lang="ts">
	import { onMount } from 'svelte';
	import { writable } from 'svelte/store';

	let isOpenlanguageMenu = writable(false);
	let isOpenlanguageMenuDropdown = writable(false);
	let isOpenBurger = writable(false);

	let languageMenu: HTMLElement;
	let languageMenuDropdown: HTMLElement;
	let burger: HTMLElement;

	onMount(() => {
		const closeList = (event: MouseEvent) => {
			if (languageMenu && !languageMenu.contains(event.target as Node)) {
				isOpenlanguageMenu.set(false);
			}
			if (languageMenuDropdown && !languageMenuDropdown.contains(event.target as Node)) {
				isOpenlanguageMenuDropdown.set(false);
			}
			if (burger && !burger.contains(event.target as Node)) {
				isOpenBurger.set(false);
			}
		};
		document.addEventListener('click', closeList);

		return () => {
			document.removeEventListener('click', closeList);
		};
	});

	function togglelanguageMenu() {
		isOpenlanguageMenu.update((value) => !value);
	}

	function togglelanguageMenuDropdown() {
		isOpenlanguageMenuDropdown.update((value) => !value);
	}

	function toggleBurger() {
		isOpenBurger.update((value) => !value);
	}

	function closeList() {
		isOpenBurger.set(false);
	}
</script>

